# -*-coding:utf-8-*-
import base64
import aes
import rsa

if __name__ == '__main__':
    aesKey = b"pI3UfYGBWXR938Ui0ngNsmOsJ675kYcfqhzG86l7K24="
    secretRsaPrivateKey = ''
    publicKey = ''
    privateKeyBase64 = aes.aes_ecb_decrypt(base64.b64decode(aesKey), base64.b64decode(secretRsaPrivateKey))
    signature = rsa.sign_by_rsa_with_sha256(base64.b64decode(privateKeyBase64), b"this is message")
    print(rsa.verify_signature(base64.b64decode(publicKey), signature, b"this is message"))
    ciphertext = aes.aes_ecb_encrypt(base64.b64decode(aesKey), b"this is message")
    print(aes.aes_ecb_decrypt(base64.b64decode(aesKey), ciphertext))
    private, public = rsa.generate_key()
    print(base64.b64encode(private).decode())
    print(base64.b64encode(public).decode())
